package chapter02;

public class Test8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a = 10;
			int b = 20;
			
			System.out.println(a > b);		// false, a가 b보다 큰 값인지 판단
			System.out.println(a < b);		// true, a가 b보다 작은 값인지 판
			System.out.println(a >= b);		// false, a가 b보다 크거나 같은 값인지 판
			System.out.println(a <= b);		// true, a가 b보다 작거나 같은 값인지 판
			System.out.println(a == b);		// false, a와 b가 같은 값인지 판
			System.out.println(a != b);		// true, a와 b가 다른 값인지 판
	}

}
